import './Frame.css';
import './GPSInfo.css';
import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, MarkerF } from '@react-google-maps/api';

const containerStyle = {
    width: '100%',
    height: '100%'
};

const initialCenter = {
    lat: 14.018000,
    lng: 120.835941
};

function GPSInfo() {
    const [location, setLocation] = useState(initialCenter);

    useEffect(() => {
        // SSE 연결
        const eventSource = new EventSource('http://127.0.0.1:5000/stream/location');

        eventSource.onmessage = (event) => {
            try {
                const responseData = JSON.parse(event.data);
                console.log('Location data from server:', responseData);

                if (responseData && responseData.location) {
                    const { lat, lng } = responseData.location;
                    setLocation({ lat, lng });
                }
            } catch (error) {
                console.error('Error parsing SSE data:', error);
            }
        };

        eventSource.onerror = (error) => {
            console.error('SSE Error:', error);
            eventSource.close();
        };

        // Cleanup
        return () => {
            eventSource.close();
        };
    }, []);

    return (
        <div className="Frame">
            <div className="FrameTitle">GPS Info</div>
            <div className="GPSInformation">
                <LoadScript googleMapsApiKey="AIzaSyAPYI0P79F58m0M5jvC8LjObBqI9tibAGo">
                    <GoogleMap mapContainerStyle={containerStyle} center={location} zoom={17}>
                        {/* 마커가 위치에 따라 정상적으로 렌더링되도록 ensure your location is correctly formatted */}
                        <MarkerF position={location} />
                    </GoogleMap>
                </LoadScript>
            </div>
        </div>
    );
}

export default GPSInfo;
