import './Frame.css';
import './LaunchState.css';
import React, { useEffect, useState } from 'react';

function LaunchState() {
  const [launchstate, setStage] = useState('LOADING');

  useEffect(() => {
    // SSE 연결
    const eventSource = new EventSource('http://127.0.0.1:5000/stream/stage');

    eventSource.onmessage = (event) => {
      try {
        const responseData = JSON.parse(event.data);
        console.log('Data from server:', responseData);
        setStage(responseData.state[0]); // 서버에서 받은 데이터를 상태에 반영
      } catch (error) {
        console.error('Error parsing SSE data:', error);
      }
    };

    eventSource.onerror = (error) => {
      console.error('SSE Error:', error);
      eventSource.close();
    };

    // Cleanup
    return () => {
      eventSource.close();
    };
  }, []);

  const handleConnect = async () => {
    try {
      await fetch('/nextStage'); // 서버에 Next Stage 명령 전송
    } catch (error) {
      console.error('Error sending Next Stage request:', error);
    }
  };

  return (
    <div className="Frame">
      <div className="FrameTitle">Launch State</div>
      <div className="Launch_State">{launchstate}</div>
      <div className="NextStageBtnDiv">
        <button className="NextStageBtn" onClick={handleConnect}>Next Stage</button>
      </div>
    </div>
  );
}

export default LaunchState;
